import ComposableArchitecture
import SwiftUI

struct BeerDetailView: View {
    let store: Store<BeerDetailState, BeerDetailAction>

    var body: some View {
        WithViewStore(
            store.scope(
                state: { $0.view },
                action: { (viewAction: BeerDetailView.Action) in
                    viewAction.feature
                }
            )
        ) { viewStore in
            VStack {
                if let beer = viewStore.beer {
                    BeerView(
                        beer: beer
                    )
                }
            }
            .onAppear {
                viewStore.send(.onAppear)
            }
        }
    }
}
