import ComposableArchitecture
import SwiftUI

struct BeerDetailView: View {
    let store: StoreOf<BeerDetailFeature>

    var body: some View {
        WithViewStore(
            store.scope(
                state: \.view,
                action: { (viewAction: BeerDetailView.Action) in
                    viewAction.feature
                }
            ),
            observe: { $0 }
        ) { viewStore in
            VStack {
                if let beer = viewStore.beer {
                    BeerView(beer: beer)
                }
            }
            .onAppear {
                viewStore.send(.onAppear)
            }
        }
    }
}
