import ComposableArchitecture
import SwiftUI

struct BeerListView: View {
    private var listStyle: some ListStyle {
        #if os(macOS)
        return SidebarListStyle()
        #else
        return DefaultListStyle()
        #endif
    }

    let store: Store<BeerListState, BeerListAction>

    var body: some View {
        WithViewStore(
            store.scope(
                state: { $0.view },
                action: { (viewAction: BeerListView.Action) in
                    viewAction.feature
                }
            )
        ) { viewStore in
            List {
//                Section {
                    ForEachStore(
                        store.scope(
                            state: \.rowStates,
                            action: BeerListAction.row
                        )) { rowViewStore in
                        BeerListRowView(store: rowViewStore)
                    }
//                }
            }
            .onAppear {
                viewStore.send(.onAppear)
            }
            .padding()
            .listStyle(listStyle)
            .navigationTitle("Beers")
//            .navigationSubtitle("üç∫")
            .toolbar {
                #if os(iOS)
                ToolbarItem {
                    EditButton()
                }
                #endif
            }
        }
    }
}
