extension BeerListRowView.Action {
    var feature: BeerListRowFeature.Action {
        switch self {
        case .onAppear:
            return .onAppear
        case let .selectBeer(beer):
            return .selectBeer(beer: beer)
        case .delete:
            return .delete
        }
    }
}
