import ComposableArchitecture
import SwiftUI

struct BeerListRowView: View {
    let store: StoreOf<BeerListRowFeature>

    var body: some View {
        WithViewStore(
            store.scope(
                state: { $0.view },
                action: { (viewAction: BeerListRowView.Action) in
                    viewAction.feature
                }
            ),
            observe: { $0 }
        ) { viewStore in
            content(beer: viewStore.beer)
                .contextMenu {
                    Button(role: .destructive, action: {
                        viewStore.send(.delete)
                    }) {
                        Label("Delete", systemImage: "trash")
                    }
                }
                .tag(viewStore.state.beer)
                .onAppear {
                    viewStore.send(.onAppear)
                }
        }
    }
}

private extension BeerListRowView {
    func content(beer: Beer) -> some View {
        VStack(alignment: .leading, spacing: 8) {
            Text(beer.name)
                .foregroundColor(.primary)
                .font(.title3)
            Text(beer.tagline)
                .foregroundColor(.secondary)
                .font(.caption)
        }
    }
}
