import ComposableArchitecture
import SwiftUI

struct AppView: View {
    let store: Store<AppState, AppAction>

    var body: some View {
        WithViewStore(
            store.scope(
                state: { $0.view },
                action: { (viewAction: AppView.Action) in
                    viewAction.feature
                }
            )
        ) { viewStore in
            NavigationView {
                // Primary
                BeerListView(
                    store: store.scope(
                        state: \.listState,
                        action: AppAction.beerList
                    )
                )

                #if os(macOS)
                if let selectedBeer = viewStore.selection {
                    NavigationDetail(beer: selectedBeer)
                }
                #endif
            }
        }
    }
}
